
public class TableEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int i,sum,j;
		
		for(i=1;i<=10;i++)
		{
			for(j=1;j<=10;j++)
			{	
				System.out.print(i +" * "+ j + " = ");
				sum=i*j;
				System.out.print(sum + "\n");
			}
			if(i==10)break;
			System.out.println();                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
			
		}
	}

}
